mapper.select:
SELECT DISTINCT CASE WHEN SUBSTRING_INDEX(CHARTN, ' ', -1) LIKE '%이다%' THEN TRIM(TRAILING '이다.' FROM SUBSTRING_INDEX(CHARTN, ' ', -1))
WHEN SUBSTRING_INDEX(CHARTN, ' ', -1) LIKE '%정' OR SUBSTRING_INDEX(CHARTN, ' ', -1) LIKE '%제' OR SUBSTRING_INDEX(CHARTN, ' ', -1) LIKE '%슐' THEN SUBSTRING_INDEX(CHARTN, ' ', -1)
ELSE NULL END AS CHARTN FROM PILL_MST WHERE (CHARTN IS NOT NULL);
